let data=[{day:"mon",amount:17.45},{day:"tue",amount:34.91},{day:"wed",amount:52.36},{day:"thu",amount:31.07},{day:"fri",amount:23.39},{day:"sat",amount:43.28},{day:"sun",amount:25.48}];const columns=document.querySelectorAll(".spending__day-col"),days=document.querySelectorAll(".spending__day-name"),totalSum=document.querySelector(".spending__total-value");function showDays(){for(let t=0;t<data.length;t++)days[t].innerHTML=`${data[t].day}`}showDays();let amount=[];function getAmount(t){for(let a=0;a<data.length;a++)t.push(data[a].amount)}getAmount(amount);let maxValue=Math.max(...amount);function columnsHeight(t){for(let a=0;a<amount.length;a++)t[a].style.height=amount[a]/maxValue*100+"%"}columnsHeight(columns);let amountSum=amount.map((t=>x+=t),x=0).reverse()[0];function showDayValue(){for(let t=0;t<data.length;t++)columns[t].dataset.dayval=amount[t]+"$"}totalSum.innerHTML=amountSum+"$",showDayValue();